import React, { useEffect, useState } from 'react';

const Profile = () => {
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);
  const [isEditing, setIsEditing] = useState(false); // Track edit mode
  const [formData, setFormData] = useState({
    username: '',
    bio: '',
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Fetch the user profile data from the backend
    fetch('http://localhost:3000/profile', { credentials: 'include' })
      .then(response => {
        if (response.ok) {
          return response.json();
        } else {
          throw new Error('Failed to fetch profile data');
        }
      })
      .then(data => {
        setUserData(data);
        setFormData({ username: data.username, bio: data.bio || '' }); // Set initial form values
      })
      .catch(error => setError(error.message));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Send updated bio and username to the backend
    fetch('http://localhost:3000/profile', {
      method: 'PUT', // Use PUT for updating the data
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify(formData),
    })
      .then(response => {
        if (response.ok) {
          setMessage('Profile updated successfully');
          setIsEditing(false); // Exit edit mode on success
          return response.json();
        } else {
          throw new Error('Failed to update profile');
        }
      })
      .then(updatedData => {
        setUserData(updatedData); // Update the userData with the latest info
      })
      .catch(error => setError(error.message));
  };

  return (
    <div className="profile-container">
      <h2>Your Profile</h2>
      {error ? (
        <p>Error: {error}</p>
      ) : userData ? (
        <div className="profile-card">
          {isEditing ? (
            <form onSubmit={handleSubmit}>
              <div className="profile-item">
                <label><strong>Username:</strong></label>
                <input
                  type="text"
                  name="username"
                  value={formData.username}
                  onChange={handleChange}
                  required
                />
              </div>
              <div className="profile-item">
                <label><strong>Bio:</strong></label>
                <textarea
                  name="bio"
                  value={formData.bio}
                  onChange={handleChange}
                  rows="3"
                />
              </div>
              <button type="submit">Save Changes</button>
              <button type="button" onClick={() => setIsEditing(false)}>Cancel</button>
            </form>
          ) : (
            <>
              <div className="profile-item">
                <strong>Username:</strong> <span>{userData.username}</span>
              </div>
              <div className="profile-item">
                <strong>Email:</strong> <span>{userData.email}</span>
              </div>
              <div className="profile-item">
                <strong>Bio:</strong> <span>{userData.bio || 'No bio available'}</span>
              </div>
              <div className="profile-item">
                <strong>Role:</strong> <span>{userData.role}</span>
              </div>
              <div className="profile-item">
                <strong>Account Created:</strong> <span>{new Date(userData.created_at).toLocaleDateString()}</span>
              </div>
              <button onClick={() => setIsEditing(true)}>Edit Profile</button>
            </>
          )}
          {message && <p>{message}</p>}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Profile;
