import React, { useState, useEffect } from 'react';

const Shop = () => {
  const [stores, setStores] = useState([]);
  const [selectedStore, setSelectedStore] = useState(null);
  const [items, setItems] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    // Fetch stores from backend
    fetch('http://localhost:3000/stores')
      .then(response => response.json())
      .then(data => setStores(data));
  }, []);

  const handleStoreClick = (storeId) => {
    // Fetch items for the selected store
    fetch(`http://localhost:3000/stores/${storeId}/items`)
      .then(response => response.json())
      .then(data => {
        setSelectedStore(storeId);
        setItems(data);
      });
  };

  const handleAddToCart = (itemId) => {
    // Add item to the cart (you'll need to implement cart functionality on the backend)
    fetch('http://localhost:3000/cart', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      credentials: 'include',
      body: JSON.stringify({ itemId, quantity: 1 })
    }).then(response => {
      if (response.ok) {
        setCart(prevCart => [...prevCart, { itemId, quantity: 1 }]);
        alert('Item added to cart');
      }
    });
  };

  return (
    <div>
      <h2>Shop</h2>
      {selectedStore ? (
        <>
          <h3>Items in Store</h3>
          <button onClick={() => setSelectedStore(null)}>Back to Stores</button>
          <ul>
            {items.map(item => (
              <li key={item.id}>
                {item.name} - ${item.price}
                <button onClick={() => handleAddToCart(item.id)}>Add to Cart</button>
              </li>
            ))}
          </ul>
        </>
      ) : (
        <>
          <h3>Available Stores</h3>
          <ul>
            {stores.map(store => (
              <li key={store.id}>
                {store.name} - {store.description}
                <button onClick={() => handleStoreClick(store.id)}>Browse</button>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
};

export default Shop;
