import React, { useState, useEffect } from 'react';
import '../../styles/ManageItems.css';

const ManageItems = () => {
  const [stores, setStores] = useState([]);  // Stores owned by the user
  const [selectedStore, setSelectedStore] = useState(null);  // Store selected by the user
  const [items, setItems] = useState([]);  // Items in the selected store
  const [itemData, setItemData] = useState({ name: '', size: '', price: '', stock: '', description: '' });  // New item data

  // Fetch stores when the component mounts
  useEffect(() => {
    fetchUserStores();
  }, []);

  // Fetch the stores owned by the user
  const fetchUserStores = async () => {
    try {
      const response = await fetch('http://localhost:3000/user/stores', { credentials: 'include' });
      if (response.ok) {
        const data = await response.json();
        setStores(data);  // Set the user's stores
      } else {
        console.error('Failed to fetch stores');
      }
    } catch (error) {
      console.error('Error fetching stores:', error);
    }
  };

  // Fetch items for the selected store
  const fetchStoreItems = async (storeId) => {
    try {
      const response = await fetch(`http://localhost:3000/store/${storeId}/items`, { credentials: 'include' });
      if (response.ok) {
        const data = await response.json();
        setItems(data);  // Set the items for the selected store
      } else {
        console.error('Failed to fetch items');
      }
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  // Handle store selection change
  const handleStoreChange = (e) => {
    const storeId = e.target.value;
    setSelectedStore(storeId);
    fetchStoreItems(storeId);  // Fetch the items for the selected store
  };

  // Handle form submission to create a new item
  const handleItemSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/items', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ ...itemData, storeId: selectedStore }),
      });

      if (response.ok) {
        console.log('Item created successfully');
        fetchStoreItems(selectedStore);  // Refresh the items after adding a new one
        setItemData({ name: '', size: '', price: '', stock: '', description: '' });  // Clear the form
      } else {
        console.error('Failed to create item');
      }
    } catch (error) {
      console.error('Error creating item:', error);
    }
  };

  // Handle deleting an item
  const handleDeleteItem = async (itemId) => {
    try {
      const response = await fetch(`http://localhost:3000/items/${itemId}`, {
        method: 'DELETE',
        credentials: 'include',
      });

      if (response.ok) {
        console.log('Item deleted successfully');
        fetchStoreItems(selectedStore);  // Refresh the items after deletion
      } else {
        console.error('Failed to delete item');
      }
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  return (
    <div className="manage-items">
      <h2>Manage Items</h2>

      {/* Store selection dropdown */}
      <label>Select a Store:</label>
      <select onChange={handleStoreChange} value={selectedStore || ''}>
        <option value="" disabled>Select your store</option>
        {stores.map((store) => (
          <option key={store.id} value={store.id}>
            {store.name}
          </option>
        ))}
      </select>

      {/* Show the items for the selected store */}
      {selectedStore && (
        <div className="store-items">
          <h3>Items in {stores.find(store => store.id === parseInt(selectedStore))?.name}</h3>
          <ul>
            {items.map(item => (
              <li key={item.id}>
                {item.name} - {item.size} - ${item.price} - Stock: {item.stock}
                <button onClick={() => handleDeleteItem(item.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Form to create a new item */}
      {selectedStore && (
        <form onSubmit={handleItemSubmit}>
          <h3>Add a New Item</h3>
          <label>Item Name:</label>
          <input
            type="text"
            value={itemData.name}
            onChange={(e) => setItemData({ ...itemData, name: e.target.value })}
            required
          />
          <label>Size:</label>
          <input
            type="text"
            value={itemData.size}
            onChange={(e) => setItemData({ ...itemData, size: e.target.value })}
            required
          />
          <label>Price:</label>
          <input
            type="number"
            value={itemData.price}
            onChange={(e) => setItemData({ ...itemData, price: e.target.value })}
            required
          />
          <label>Stock:</label>
          <input
            type="number"
            value={itemData.stock}
            onChange={(e) => setItemData({ ...itemData, stock: e.target.value })}
            required
          />
          <label>Description:</label>
          <textarea
            value={itemData.description}
            onChange={(e) => setItemData({ ...itemData, description: e.target.value })}
            required
          ></textarea>
          <button type="submit">Add Item</button>
        </form>
      )}
    </div>
  );
};

export default ManageItems;
