import React, { useState, useEffect } from 'react';

const ManageStore = ({ store, fetchStore }) => {
  const [storeData, setStoreData] = useState({
    name: store ? store.name : '',
    description: store ? store.description : ''
  });

  useEffect(() => {
    if (store) {
      setStoreData({ name: store.name, description: store.description });
    }
  }, [store]);

  const handleStoreSubmit = async (e) => {
    e.preventDefault();
    const method = store ? 'PUT' : 'POST';  // POST to create, PUT to update
    const url = store ? 'http://localhost:3000/store' : 'http://localhost:3000/store';

    try {
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(storeData),
      });
      if (response.ok) {
        fetchStore();  // Refresh store info after submit
      }
    } catch (error) {
      console.error("Error saving store:", error);
    }
  };

  return (
    <section className="manage-store">
      <h2>Manage Store</h2>
      {!store ? (
        <form onSubmit={handleStoreSubmit}>
          <label>Store Name: </label>
          <input
            type="text"
            name="name"
            value={storeData.name}
            onChange={(e) => setStoreData({ ...storeData, name: e.target.value })}
            required
          />
          <label>Description: </label>
          <textarea
            name="description"
            value={storeData.description}
            onChange={(e) => setStoreData({ ...storeData, description: e.target.value })}
            required
          ></textarea>
          <button type="submit">Create Store</button>
        </form>
      ) : (
        <div>
          <h3>{store.name}</h3>
          <p>{store.description}</p>
          <form onSubmit={handleStoreSubmit}>
            <label>Update Store Name: </label>
            <input
              type="text"
              name="name"
              value={storeData.name}
              onChange={(e) => setStoreData({ ...storeData, name: e.target.value })}
              required
            />
            <label>Update Description: </label>
            <textarea
              name="description"
              value={storeData.description}
              onChange={(e) => setStoreData({ ...storeData, description: e.target.value })}
              required
            ></textarea>
            <button type="submit">Update Store</button>
          </form>
        </div>
      )}
    </section>
  );
};

export default ManageStore;
