import React, { useState } from 'react';
import '../../styles/UserDashboard.css';  // Correct the import path
import Profile from './Profile';
import Shop from './Shop';
import Cart from './Cart';
import { useNavigate } from 'react-router-dom'; // To navigate for logout

const UserDashboard = () => {
  const [view, setView] = useState('profile'); // Track which view is selected
  const navigate = useNavigate();

  // Function to handle logout
  const handleLogout = () => {
    // Clear session or token (this can depend on how you're managing sessions)
    fetch("http://localhost:3000/logout", {
      method: 'POST',
      credentials: 'include' // Send session cookie for logout
    }).then(() => {
      // Redirect to homepage after logout
      navigate('/');
    });
  };

  return (
    <div className="dashboard-container">
      <aside className="dashboard-sidebar">
        <button onClick={() => setView('profile')}>Profile</button>
        <button onClick={() => setView('shop')}>Shop</button>
        <button onClick={() => setView('cart')}>Cart</button>
        <button onClick={handleLogout}>Logout</button>
      </aside>

      <main className="dashboard-content">
        {view === 'profile' && <Profile />} {/* Show Profile view */}
        {view === 'shop' && <Shop />} {/* Show Shop view */}
        {view === 'cart' && <Cart />} {/* Show Cart view */}
      </main>
    </div>
  );
};

export default UserDashboard;
