import React, { useState } from "react";
import { Link } from "react-router-dom"; // Add Link for navigation
import "../../styles/Login.css"; // Apply new design styles

const Login = () => {
  const [formData, setFormData] = useState({
    email: "",
    password: "",
  });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Make an API call to log in the user
    try {
      const response = await fetch("http://localhost:3000/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        // Check the user's role and navigate to the appropriate page
        if (result.role === "user") {
          navigate("/dashboard"); // Redirect user to user dashboard
        } else if (result.role === "store_owner") {
          navigate("/store-owner-dashboard"); // Redirect store owner to their dashboard
        } else if (result.role === "admin") {
          navigate("/admin-dashboard"); // Redirect admin to their dashboard
        }
      } else {
        setMessage(result.message); // Display error message
      }
    } catch (error) {
      setMessage("Error occurred during login.");
    }
  };

  return (
    <div className="auth-container">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <label>Email:</label>
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          required
        />
        <br />
        <label>Password:</label>
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          required
        />
        <br />
        <button type="submit">Login</button>
      </form>
      <p>{message}</p>

      <p className="switch-link">
        Don't have an account? <Link to="/signup">Sign Up</Link>
      </p>
    </div>
  );
};

export default Login;
