import React, { useState, useEffect } from 'react';
import '../../styles/Cart.css'; 
const Cart = () => {
  const [cartItems, setCartItems] = useState([]);

  useEffect(() => {
    // Fetch cart items from backend
    fetch('http://localhost:3000/cart', { credentials: 'include' })
      .then(response => response.json())
      .then(data => setCartItems(data));
  }, []);

  const handleRemoveItem = (itemId) => {
    // Remove item from the cart in the backend
    fetch(`http://localhost:3000/cart/${itemId}`, {
      method: 'DELETE',
      credentials: 'include'
    }).then(response => {
      if (response.ok) {
        setCartItems(prevItems => prevItems.filter(item => item.id !== itemId));
      } else {
        alert('Failed to remove item');
      }
    });
  };

  return (
    <div className="cart-container">
      <h2>Your Cart</h2>
      {cartItems.length > 0 ? (
        <ul className="cart-items">
          {cartItems.map(item => (
            <li key={item.id} className="cart-item">
              <span>{item.name} - {item.quantity} x ${item.price}</span>
              <button onClick={() => handleRemoveItem(item.id)} className="remove-button">Remove</button>
            </li>
          ))}
        </ul>
      ) : (
        <p>Your cart is empty.</p>
      )}
    </div>
  );
};

export default Cart;
