import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import Profile from './Profile';
import '../../styles/StoreOwnerDashboard.css';

const StoreOwnerDashboard = () => {
  const [store, setStore] = useState(null);  // Store information
  const [items, setItems] = useState([]);    // Store items
  const [activeSection, setActiveSection] = useState('profile');  // To control which section is displayed

  // Fetch the store info for the store owner
  useEffect(() => {
    fetchStore();
  }, []);

  const fetchStore = async () => {
    try {
      const response = await fetch('http://localhost:3000/store', { credentials: 'include' });
      if (response.ok) {
        const data = await response.json();
        setStore(data);
      }
    } catch (error) {
      console.error("Failed to fetch store data:", error);
    }
  };

  // Function to add or update the store
  const handleStoreSubmit = async (e) => {
    e.preventDefault();
    const storeData = {
      name: e.target.name.value,
      description: e.target.description.value,
    };

    try {
      const response = await fetch('http://localhost:3000/store', {
        method: store ? 'PUT' : 'POST',  // POST to create, PUT to update
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(storeData),
      });
      if (response.ok) {
        fetchStore();  // Refresh store info after submit
      }
    } catch (error) {
      console.error("Error saving store:", error);
    }
  };

  // Function to create a new item
  const handleCreateItem = async (e) => {
    e.preventDefault();
    const itemData = {
      name: e.target.itemName.value,
      size: e.target.size.value,
      price: e.target.price.value,
      stock: e.target.stock.value,
      description: e.target.itemDescription.value,
    };

    try {
      const response = await fetch('http://localhost:3000/items', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        credentials: 'include',
        body: JSON.stringify(itemData),
      });
      if (response.ok) {
        fetchItems();  // Refresh items after adding a new one
      }
    } catch (error) {
      console.error("Error adding item:", error);
    }
  };

  // Fetch items for the store
  const fetchItems = async () => {
    try {
      const response = await fetch(`http://localhost:3000/store/${store.id}/items`, { credentials: 'include' });
      if (response.ok) {
        const data = await response.json();
        setItems(data);  // Set items in the state
      }
    } catch (error) {
      console.error("Failed to fetch items:", error);
    }
  };

  return (
    <div className="dashboard-container">
      <aside className="dashboard-sidebar">
        <h2 className="dashboard-title">Store Owner Dashboard</h2>
        <button onClick={() => setActiveSection('profile')}>Manage Profile</button>
        <button onClick={() => setActiveSection('store')}>Manage Store</button>
        <button onClick={() => setActiveSection('items')}>Manage Items</button>
        <Link to="/"><button>Logout</button></Link>
      </aside>

      <main className="dashboard-content">
        {/* Conditionally render the sections based on activeSection */}
        {activeSection === 'profile' && <Profile />}
        
        {activeSection === 'store' && (
          <section className="manage-store">
            <h2>Manage Store</h2>
            {!store ? (
              <form onSubmit={handleStoreSubmit}>
                <label>Store Name: </label>
                <input type="text" name="name" required />
                <label>Description: </label>
                <textarea name="description" required></textarea>
                <button type="submit">Create Store</button>
              </form>
            ) : (
              <div>
                <h3>{store.name}</h3>
                <p>{store.description}</p>
                <form onSubmit={handleStoreSubmit}>
                  <label>Update Store Name: </label>
                  <input type="text" name="name" defaultValue={store.name} required />
                  <label>Update Description: </label>
                  <textarea name="description" defaultValue={store.description} required></textarea>
                  <button type="submit">Update Store</button>
                </form>
              </div>
            )}
          </section>
        )}

        {activeSection === 'items' && store && (
          <section className="manage-items">
            <h2>Manage Items</h2>
            <form onSubmit={handleCreateItem}>
              <label>Item Name: </label>
              <input type="text" name="itemName" required />
              <label>Size: </label>
              <input type="text" name="size" required />
              <label>Price: </label>
              <input type="number" name="price" step="0.01" required />
              <label>Stock: </label>
              <input type="number" name="stock" required />
              <label>Description: </label>
              <textarea name="itemDescription" required></textarea>
              <button type="submit">Add Item</button>
            </form>

            {/* List existing items */}
            <div className="item-list">
              <h3>Your Items</h3>
              {items.length === 0 ? (
                <p>No items added yet.</p>
              ) : (
                <ul>
                  {items.map(item => (
                    <li key={item.id}>
                      {item.name} - {item.size} - ${item.price} - Stock: {item.stock}
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </section>
        )}
      </main>
    </div>
  );
};

export default StoreOwnerDashboard;
