import React, { useState, useEffect } from 'react';

const ManageStores = () => {
  const [stores, setStores] = useState([]);

  useEffect(() => {
    fetchStores();
  }, []);

  const fetchStores = async () => {
    try {
      const response = await fetch('http://localhost:3000/admin/stores', { credentials: 'include' });
      if (response.ok) {
        const data = await response.json();
        setStores(data);
      } else {
        console.error('Failed to fetch stores');
      }
    } catch (error) {
      console.error('Error fetching stores:', error);
    }
  };

  const handleDeleteStore = async (storeId) => {
    try {
      const response = await fetch(`http://localhost:3000/admin/stores/${storeId}`, {
        method: 'DELETE',
        credentials: 'include',
      });
      if (response.ok) {
        setStores(stores.filter(store => store.id !== storeId));  // Remove store from state
        console.log('Store deleted successfully');
      } else {
        console.error('Failed to delete store');
      }
    } catch (error) {
      console.error('Error deleting store:', error);
    }
  };

  return (
    <div>
      <h2>Manage Stores</h2>
      <ul>
        {stores.map(store => (
          <li key={store.id}>
            {store.name} - {store.description}
            <button onClick={() => handleDeleteStore(store.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ManageStores;
